import React from 'react';
import { useAuthState } from "react-firebase-hooks/auth"
import { auth } from "../../firebase";
import SignIn from './SignIn';
import Line from "./Line";

function LineApp() {
  const [user] = useAuthState(auth);
  return (
    <div>
      { user ? <Line /> : <SignIn /> }
    </div>  
  );
}
export default LineApp;
