import React from 'react';
import { useAuthState } from "react-firebase-hooks/auth"
import { auth } from "../../firebase";
import './App.css';
import SignIn from './components/SignIn';
import Line from "./Line";

function App() {
  const [user] = useAuthState(auth);
  return (
    <div>
      { user ? <Line /> : <SignIn /> }
    </div>  
  );
}
export default App;
