import './App.css';
import { auth } from "./firebase";
import { useAuthState } from "react-firebase-hooks"
import SignIn from './components/SignIn';
import Line from "./components/Line";

function App() {
  const user = useAuthState(auth);
  return (
    <div>
      { user ? <Line /> : <SignIn /> }
    </div>
  );
}

export default App;
